// definition_test.go contains unit tests for exported functions.
package definition

import (
	"encoding/json"
	"fmt"
	"testing"
	"time"
)

// TestNewBad tests New on bad args.
func TestNewBad(t *testing.T) {
	badArgs := []string {
		"",
		" ",
		"..",
		"!",
		"/",
		"//",
		"../../..",
		"f8asdfijhasfonh48oao8aonfo78",
		"bonjour123",
		"bonjour/cava",
		"larousse.fr/dictionnaire/francais/arbre/4974",
		"larousse.fr/dictionnaires/dskjfhss/arbre/4974",
		"larousse.fr/dictionnaires/coréen",
		"larousse.fr/dictionnaires/francais",
		"larousse.fr/dictionnaires/francais/aaaaaaaaaaa",
		"larousse.fr/dictionnaires/francais/3289723985",
		"larousse.fr/dictionnaires/francais/aaa/bbb",
		"larousse.fr/dictionnaires/francais/aaa/bbb/ccc",
		"larousse.fr/dictionnaires/francais/aaa/123",
		"larousse.fr/dictionnaires/francais/arbre/4974987942398734",
		"larousse.fr/dictionnaires/francais/francais/francais",
	}
	
	for _, b := range badArgs {
		time.Sleep(5 * time.Second)
		fmt.Print(b, "\t")
		_, err := New(b)
		if err == nil {
			fmt.Println("FAIL - should be rejected")
			t.Fail()
		} else {
			fmt.Println("OK")
		}
	}
}

// TestNew tests New on various words.
func TestNew(t *testing.T) {
	words := []string{
		"arbre",
		"beau",
		"couper",
		"vert",
		"informatique",
	}
	
	for _, w := range words {
		time.Sleep(5*time.Second)
		fmt.Print(w, "\t")
		
		got, err := New(w)
		if err != nil {
			t.Fatal(err)
		}
		
		want, err := getCorrectResult(w)
		if err != nil {
			t.Fatal(err)
		}
		
		message, ok := want.equals(got)
		if !ok {
			fmt.Printf("FAIL\n%s\n\n", message)
			t.Fail()
		} else {
			fmt.Println("OK")
		}
	}
}

