// traduction_test.go contains unit tests for exported functions.
package traduction

import (
	"encoding/json"
	"fmt"
	"testing"
)

// Type newArg represents args passed to New.
type newArg struct {
	word string
	from Language
	to   Language
}

func (na newArg) String() string {
	return fmt.Sprintf("(%s, %d [%s], %d [%s])", na.word, na.from, na.from, na.to, na.to)
}

// TestNewBad tests New on bad args.
func TestNewBad(t *testing.T) {
	badArgs := []newArg{
		{"bothen", En, En},
		{"bothfr", Fr, Fr},
		{"unknownfrom", En-32523, Fr},
		{"unknownto", En, Fr+83523},
		{"", En, Fr},
	}
	
	for _, b := range badArgs {
		fmt.Print(b, "\t")
		_, err := New(b.word, b.from, b.to)
		if err == nil {
			fmt.Println("FAIL - should be rejected")
			t.Fail()
		} else {
			fmt.Println("OK")
		}
	}
}

// TestNew tests the exported function New.
// 
// Because New is essentially a wrapper around NewFromFileOrURL, it doesn't
// need many tests.
func TestNew(t *testing.T) {
	goodArgs := []newArg {
		{"digital", En, Fr},
		{"roquette", Fr, En},
	}
	
	for _, g := range goodArgs {
		fmt.Print(g, "\t")
		got, err := New(g.word, g.from, g.to)
		if err != nil {
			t.Fatal(err)
		}
		
		want, err := getCorrectResult(g.word)
		if err != nil {
			t.Fatal(err)
		}
		
		message, ok := want.equals(got)
		if !ok {
			fmt.Printf("FAIL\n%s\n\n", message)
			t.Fail()
		} else {
			fmt.Println("OK")
		}
	}
}

