// TestNewFromFileOrURL tests the exported function NewFromFileOrURL on various
// URLs.
func TestNewFromFileOrURL(t *testing.T) {
	table := map[string]string {
		"https://www.larousse.fr/dictionnaires/francais-anglais/aire/1944":         "aire",
		"https://www.larousse.fr/dictionnaires/francais-anglais/bois/9914":         "bois",
		"https://www.larousse.fr/dictionnaires/francais-anglais/certificat/648464": "certificat",
		"https://www.larousse.fr/dictionnaires/francais-anglais/coup/19682":        "coup",
		"https://www.larousse.fr/dictionnaires/francais-anglais/couper/19720":      "couper",
		"https://www.larousse.fr/dictionnaires/francais-anglais/roquette/69102":    "roquette",
		"https://www.larousse.fr/dictionnaires/francais-anglais/rosacée/69108":     "rosacée",
		"https://www.larousse.fr/dictionnaires/francais-anglais/vert/80698":        "vert",
		
		"https://www.larousse.fr/dictionnaires/anglais-francais/motivate/596253":   "motivate",
		"https://www.larousse.fr/dictionnaires/anglais-francais/blue/566219":       "blue",
		"https://www.larousse.fr/dictionnaires/anglais-francais/digital/575525":    "digital",
		"https://www.larousse.fr/dictionnaires/anglais-francais/make/593701":       "make",
		"https://www.larousse.fr/dictionnaires/anglais-francais/drink/577016":      "drink",
	}
	
	for url, word := range table {
		fmt.Print(url, "\t")
		
		got, err := NewFromFileOrURL(url)
		if err != nil {
			t.Fatal(err)
		}
		
		want, err := getCorrectResult(word)
		if err != nil {
			t.Fatal(err)
		}
		
		message, ok := want.equals(got)
		if !ok {
			fmt.Printf("FAIL\n%s\n\n", message)
			t.Fail()
		} else {
			fmt.Println("OK")
		}
	}
}

